jQuery(document).ready( function($){

    /*
     * Show meta boxes based upon the page template
     */
    $('select#page_template').change( function(){

        search = $(this).val().replace(".php", "");

        $('.only-in-page-template').slideUp(500);
        $('.only-in-page-template.only-in-'+ search).slideDown(500);

        if ( $('.only-in-page-template.only-in-'+ search).hasClass('hide-content') ) {
            $("#postdivrich").slideUp(500);
        } else {
            $("#postdivrich").slideDown(500);
        }

        if ( $('.only-in-page-template.only-in-'+ search).hasClass('hide-title') ) {
            $("#titlediv").slideUp(500);
        } else {
            $("#titlediv").slideDown(500);
        }

    });
    $('select#page_template').change();

});